import ChatBot from "react-simple-chatbot";
import React, { Component, useEffect, useState } from "react";
import { ThemeProvider } from "styled-components";
import { render } from "@testing-library/react";
import { useDispatch } from "react-redux";
import { answer } from "../reducer/chatbotSlice";
import { func } from "prop-types";

export default function Chat() {
  return (
    <ThemeProvider
      theme={{
        background: "#f5f8fb",
        fontFamily: "Helvetica Neue",
        headerBgColor: "#EF6C00",
        headerFontColor: "#fff",
        headerFontSize: "15px",
        botBubbleColor: "#EF6C00",
        botFontColor: "#fff",
        userBubbleColor: "#fff",
        userFontColor: "#4a4a4a",
      }}
    >
      <ChatBot
        steps={[
          {
            id: 'welcome',
            message: '안녕하세요~ 코핀 챗봇이에요 궁금한 사항이 있나요?',
            trigger: 'select',
          },
          { 
            id: "select", 
            options: [ 
              { value: "y", label: "백신 접종 자가 체크리스트", trigger: "askBot" }, 
              { value: "n", label: "질문하기", trigger: "question" }, 
            ], 
          }, 
          {
            id: 'question',
            message: '질문하실 말이 있나요?',
            trigger: 'userinput'
          },
          {
            id: 'userinput',
            user: true,
            trigger: 'askBot'
          },
          
          {
            id: 'dynamicallyReachedStep',
            message: 'This step was reached dynamically.',
            trigger: 'userinput'
          },
          {
            id: "askBot",
            component: <Ask />,
            asMessage: true,
            trigger: 'userinput'
          },
          {
            id: 'askFever',
            message: '현재 발열증상이 있나요?',
            trigger: 'fever'
          },
          {
            id: 'fever',
            options: [ 
              { value: "y", label: "백신 접종 자가 체크리스트", trigger: "askBot" }, 
              { value: "n", label: "질문하기", trigger: "question" }, 
            ], 
          },
          {
            id: '',
            message: '?',
            trigger: ''
          },
          {
            id: '',
            options: [ 
              { value: "y", label: "백신 접종 자가 체크리스트", trigger: "askBot" }, 
              { value: "n", label: "질문하기", trigger: "question" }, 
            ], 
          },
          {
            id: '',
            message: '질문 내용이 {previousValue}(이)가 맞습니까?',
            trigger: ''
          },
        ]}
        botAvatar={require("./data/icon.png").default}
        userAvatar={require("./data/user.png").default}
      />
    </ThemeProvider>
  );
};

export function Ask(props){
  const dispatch = useDispatch()
  const [ text, setText] = useState()
  console.log(props.steps)
  // if (props.steps.userinput == null){
    // console.log(props.steps.submit.value)
  // }else{
    // console.log(props.steps.userinput.value)
  // }
  // useEffect(()=>{
  //   setText(props.steps.userinput.value)
  //   // dispatch(answer(props.steps.userinput.value))
  // },[text])
    return (
      <>
      {/* <div>{text}이(가) 라고 질문하신 것이 맞습니까?</div> */}
      <div>이(가) 라고 질문하신 것이 맞습니까?</div>
      <button onClick={() => props.triggerNextStep({trigger: 'dynamicallyReachedStep'})}>
    발열 있음
  </button>
      </>
    );
}


export function CheckStatus(props){
  const dispatch = useDispatch()
  const [ choice, setChoice] = useState()
  console.log(props.steps)
  // if (props.steps.userinput == null){
    // console.log(props.steps.submit.value)
  // }else{
    // console.log(props.steps.userinput.value)
  // }
  // useEffect(()=>{
  //   setText(props.steps.userinput.value)
  //   // dispatch(answer(props.steps.userinput.value))
  // },[text])
    return (
      <>
      {/* <div>{text}이(가) 라고 질문하신 것이 맞습니까?</div> */}
      <div>{} 을 선택하셨습니다.</div>
      <button onClick={() => props.triggerNextStep({trigger: 'dynamicallyReachedStep'})}>
    발열 있음
  </button>
      </>
    );
}
