import { Map, MapMarker } from 'react-kakao-maps-sdk';
import React, {useState, useMemo} from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from 'react-redux';
import { casesPoints, medPoints } from "../reducer/mapSlice";
const { kakao } = window; 

function LocalMap() {
    const dispatch = useDispatch()
    var geoInfo = window.localStorage.getItem('sessionGeo').split(',')
    const [map, setMap] = useState()
    const points = useSelector(state => state.map.mapsState.map(
        x => {return {title: x.name,
                      latlng: {lat: x.latitude, lng:x.longitude}}}
    )) 
    // const [points, setPoints] = useState([
    //   {lat: 33.452278, lng:126.567803},
    //   {lat: 33.452671, lng:126.574792},
    //   {lat: 33.451744, lng:126.572441},
    // ])


    const bounds = useMemo(() => {
      const bounds = new kakao.maps.LatLngBounds();

      points.forEach(point => {
        bounds.extend(new kakao.maps.LatLng(point.latlng.lat, point.latlng.lng))
      });
      return bounds;
    }, [points])

    return(<>
    <Map // 지도를 표시할 Container
          center={{
            // 지도의 중심좌표
            lat: geoInfo[0],
            lng: geoInfo[1],
          }}
          style={{
            width: "1000px",
            height: "800px",
          }}
          level={5} // 지도의 확대 레벨
          onCreate={setMap}
        >
          {points.map(point => <MapMarker key={`${point.lat}-${point.lng}`} position={point} title='내 위치'/>)}
        </Map>
        <OptionUl>
        <li>
            <label>표시 대상 선택</label><br/>
            <select id="sel" onChange={(e) => {
            if (e.target.value == 'medpoint'){
                dispatch(medPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            }else{
                dispatch(casesPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            }
            if (map) map.setBounds(bounds)
          }}>
                <optgroup label="표시 대상 선택">
                  <option value="medpoint">선별 진료소</option>  
                  <option value="cases">확진자 발생 위치</option>  
                </optgroup>
            </select>
        </li>
    </OptionUl>
    </>);
}

export default LocalMap;

const OptionUl = styled.ul`
    list-style: none;
`
