import ChatBot from 'react-simple-chatbot';
import React, { Component } from 'react';
import { ThemeProvider } from 'styled-components';

// all available props
const theme = {
  background: '#f5f8fb',
  fontFamily: 'Helvetica Neue',
  headerBgColor: '#EF6C00',
  headerFontColor: '#fff',
  headerFontSize: '15px',
  botBubbleColor: '#EF6C00',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
};


const Chat4 = () => {
  return(
  <ThemeProvider theme={theme}>
    <ChatBot
   steps={[
    {
      id:'q-firstname', 
      message:'What is your first name?', 
      trigger:'firstname',
    },
    {
      id:'firstname', 
      user:true,
      trigger:'q-lastname'
    },
    {
      id:'q-lastname', 
      message:'What is your last name?', 
      trigger:'lastname',
    },
    {
      id:'lastname', 
      user:true,
      trigger:'q-email'
    },
    {
      id:'q-email', 
      message:'Finally. what is you email?', 
      trigger:'email',
    },
    {
      id:'email', 
      user:true,
      trigger:'q-submit'
    },
    {
      id:'q-submit', 
      message:'Do you wish to submit?', 
      trigger:'submit'
    },
    {
      id:'submit', 
      options:[
      {value:'y', label:'Yes', trigger:'end-message'},
      {value:'n', label:'No', trigger:'no-submit'},
      ] 
    },
    {
            id: 'no-submit',
            message:'Your information was not submitted.', 
            end: true,
         },
              {
            id: 'end-message',
            component: <Post />,
            asMessage: true,
            end: true,
         },
  ]}
  botAvatar={require("./data/icon.png").default} 
  userAvatar={require("./data/user.png").default}
/>
  </ThemeProvider>
  )
}

export default Chat4



export class Post extends Component {
    constructor(props) {
        super(props);
        const { steps } = this.props;
        const { submit, firstname, lastname, email } = steps;
    
        this.state =  { submit, firstname, lastname, email }; 
      }
    
    
      componentDidMount() {
        const userObject = {
          submit:this.state.submit.value,
          first_name:this.state.firstname.value,
          last_name:this.state.lastname.value,
          email:this.state.email.value,
        };
        alert(`data: ${JSON.stringify(userObject)}`)
      }
    
      render() {
        return (<>
            <div>Thank you! Your data was submitted successfully!</div>
          </>
          );
        }
      };
