import ChatBot from "react-simple-chatbot";
import React, { useEffect, useState } from "react";
import { ThemeProvider } from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { answer, answerStatus, initStatus } from "../reducer/chatbotSlice";

export default function Chat() {
  const dispatch = useDispatch()
  dispatch(initStatus())
  return (
    <ThemeProvider
      theme={{
        background: "#f5f8fb",
        fontFamily: "Helvetica Neue",
        headerBgColor: "#EF6C00",
        headerFontColor: "#fff",
        headerFontSize: "15px",
        botBubbleColor: "#EF6C00",
        botFontColor: "#fff",
        userBubbleColor: "#fff",
        userFontColor: "#4a4a4a",
      }}
    >
      <ChatBot
        steps={[
          {
            id: 'welcome',
            message: '안녕하세요~ 코핀 챗봇이에요 궁금한 사항이 있나요?',
            trigger: 'select',
          },
          { 
            id: "select", 
            options: [ 
              { value: "y", label: "백신 접종 자가 체크리스트", trigger: "fever" }, 
              { value: "n", label: "질문하기", trigger: "question" }, 
            ], 
          }, 
          {
            id: 're_quest',
            message: '또 다른 궁금하신 부분이 있나요?',
            trigger: 'userinput'
          },
          {
            id: 'question',
            message: '질문하실 말이 있나요?',
            trigger: 'userinput'
          },
          {
            id: 'userinput',
            user: true,
            trigger: 'askBot'
          },
          
          {
            id: 'dynamicallyReachedStep',
            message: 'This step was reached dynamically.',
            trigger: 'userinput'
          },
          {
            id: "askBot",
            component: <Ask />,
            asMessage: true,
            trigger: 'userinput'
          },
          {
            id: "checkBot",
            component: <CheckStatus />,
            asMessage: true,
            trigger: 'userinput'
          },
          {
            id: 'fever',
            message: '현재 발열증상이 있나요?',
            trigger: 'select_fever'
          },
          {
            id: 'select_fever',
            options: [ 
              { value: "fever1", label: "38.4℃ 이하", trigger: "checkBot" }, 
              { value: "fever2", label: "38.5-38.9℃", trigger: "checkBot" }, 
              { value: "fever3", label: "39.0-40℃", trigger: "checkBot" }, 
              { value: "fever4", label: "40.1℃ 이상", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "ino_area" }, 
            ], 
          },
          {
            id: 'ino_area',
            message: '접종 부위에 통증이 있나요?',
            trigger: 'select_ino_area'
          },
          {
            id: 'select_ino_area',
            options: [ 
              { value: "ino_area1", label: "통증은 있으나 약 먹을 정도는 아님", trigger: "checkBot" }, 
              { value: "ino_area2", label: "1~2일 약을 먹어야 팔을 움직이는데 지장이 없음", trigger: "checkBot" }, 
              { value: "ino_area3", label: "3일 이상 통증이 지속되거나 약을 먹어도 통증조절이 안되어 팔을 움직이기 어려운 경우", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "swell" }, 
            ], 
          },
          {
            id: 'swell',
            message: '접종부위에 부어오름 증상이 있나요?',
            trigger: 'select_swell'
          },
          {
            id: 'select_swell',
            options: [ 
              { value: "swell1", label: "직경 5cm 이하", trigger: "checkBot" }, 
              { value: "swell2", label: "5.1~10cm 움직이기 불편", trigger: "checkBot" }, 
              { value: "swell3", label: "10cm 이상 활동 어려움", trigger: "checkBot" }, 
              { value: "swell4", label: "농양", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "sick" }, 
            ], 
          },
          {
            id: 'sick',
            message: '구토감 또는 매스꺼움 증상이 있나요?',
            trigger: 'select_sick'
          },
          {
            id: 'select_sick',
            options: [ 
              { value: "sick1", label: "일상생활이 불편한 정도는 아님", trigger: "checkBot" }, 
              { value: "sick2", label: "하루(24시간)에 1~2회 발생", trigger: "checkBot" }, 
              { value: "sick3", label: "하루(24시간)에 3회 이상 발생", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "pain" }, 
            ], 
          },
          {
            id: 'pain',
            message: '통증이 있나요?',
            trigger: 'select_pain'
          },
          {
            id: 'select_pain',
            options: [ 
              { value: "pain3", label: "두통", trigger: "checkBot" }, 
              { value: "pain4", label: "관절통", trigger: "checkBot" }, 
              { value: "pain5", label: "근육통", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "fatigue" }, 
            ], 
          },
          {
            id: 'fatigue',
            message: '피로감이 증상이 있나요?',
            trigger: 'select_fatigue'
          },
          {
            id: 'select_fatigue',
            options: [ 
              { value: "fatigue1", label: "피로감 증상 있음", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "allergy" }, 
            ], 
          },
          {
            id: 'allergy',
            message: '알레르기 증상이 있나요?',
            trigger: 'select_allergy'
          },
          {
            id: 'select_allergy',
            options: [ 
              { value: "allergy3", label: "발진", trigger: "checkBot" }, 
              { value: "allergy4", label: "두드러기", trigger: "checkBot" }, 
              { value: "allergy5", label: "소양감(가려움증)", trigger: "checkBot" }, 
              { value: "allergy6", label: "부기(얼굴/입술)", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "etc" }, 
            ], 
          },
          {
            id: 'etc',
            message: '기타 이상 반응이 있나요?',
            trigger: 'select_etc'
          },
          {
            id: 'select_etc',
            options: [ 
              { value: "etc3", label: "기타 이상 반응", trigger: "checkBot" }, 
              { value: "none", label: "해당 없음", trigger: "re_quest" }, 
            ], 
          },
        ]}
        botAvatar={require("./data/icon.png").default}
        userAvatar={require("./data/user.png").default}
      />
    </ThemeProvider>
  );
};

export function Ask(props){
  const dispatch = useDispatch()
  const [ text, setText] = useState()
  console.log(props.steps)
  // if (props.steps.userinput == null){
    // console.log(props.steps.submit.value)
  // }else{
    // console.log(props.steps.userinput.value)
  // }
  useEffect(()=>{
    setText(props.steps.userinput.value)
    dispatch(answer(props.steps.userinput.value))
  },[text])
    return (
      <>
      <div>{text}이(가) 라고 질문하신 것이 맞습니까?</div>
      {text == "체크리스트" ? <button onClick={() => props.triggerNextStep({trigger: 'fever'})} 
      style={{border:"0.5px", background:'burlywood', borderRadius: "9px", margin: "1em 2em 5px 2em", cursor: "pointer", boxShadow: "1px 1px 3px 1px #cbbab0"}} >
        백신 접종 증상 체크하기!
      </button>: <></>}
      {/* <div>이(가) 라고 질문하신 것이 맞습니까?</div> */}
      </>
    );
}


export function CheckStatus(props){
  const dispatch = useDispatch()
  const pain_arr = ['fever', 'ino_area', 'swell', 'sick', 'pain', 'fatigue', 'allergy', 'etc', 'welcome']
  const symptom = props.previousStep.value.slice(0, -1)
  const choice = useSelector((state)=>state.chatbot.healthStates[symptom])
  console.log(props.previousStep.value.slice(-1)==1)
  useEffect(()=>{
    dispatch(answerStatus({'symptom': symptom, 'details':props.previousStep.message}))
  },[])
    return (
      <>
      <div>{choice['answer']}</div>
      <button style={{border:"0.5px", background:'burlywood', borderRadius: "9px", margin: "0.7em auto", 
                      cursor: "pointer", boxShadow: "1px 1px 3px 1px #cbbab0", width: "10em", display: "block"}} 
              onClick={() => {
                try {
                  props.triggerNextStep({trigger: pain_arr[pain_arr.indexOf(symptom)+1]})
                } catch (error) {
                }
              }}>
        이어서 진행하기
      </button>
      <button style={{border:"0.5px", background:'burlywood', borderRadius: "9px", margin: "0.5em auto", 
                      cursor: "pointer", boxShadow: "1px 1px 3px 1px #cbbab0", width: "10em", display: "block"}} 
              onClick={() => {
                  props.triggerNextStep({trigger: 're_quest'})
              }}>
        그만하기
      </button>
      </>
    );
}
