import ChatBot from 'react-simple-chatbot';
import React, { Component } from 'react';
import { ThemeProvider } from 'styled-components';

// all available props
const theme = {
  background: '#f5f8fb',
  fontFamily: 'Helvetica Neue',
  headerBgColor: '#EF6C00',
  headerFontColor: '#fff',
  headerFontSize: '15px',
  botBubbleColor: '#EF6C00',
  botFontColor: '#fff',
  userBubbleColor: '#fff',
  userFontColor: '#4a4a4a',
};


const Chat4 = () => {
  return(
  <ThemeProvider theme={theme}>
    <ChatBot
  steps={[
    {
      id: '1',
      message: 'What is your name?',
      trigger: '2',
    },
    {
      id: '2',
      user: true,
      trigger: '3'
    },
    {
      id: '3',
      message: 'Hi {previousValue}, nice to meet you!',
      trigger: '4'
    },
    {
        id: '4',
        message: 'How may I help you?',
        trigger: '5'
    },
    {
        id: '5',
        user: true,
        trigger: '6'
      },
    {
        id: '6',
        // message: "I'm sorry, but I don't know '{previousValue}'.",
        component: <Post/>,
        trigger: '4'
      },
  ]} 
  botAvatar={require("./data/icon.png").default} 
  userAvatar={require("./data/user.png").default}
/>
  </ThemeProvider>
  )
}

export default Chat4



export class Post extends Component {
    constructor(props) {
        super(props);
        const { steps } = this.props;
        const { submit, firstname, lastname, email } = steps;
    
        this.state =  { submit, firstname, lastname, email }; 
      }
    
    
      componentDidMount() {
        const userObject = {
          submit:this.state.submit.value,
          first_name:this.state.firstname.value,
          last_name:this.state.lastname.value,
          email:this.state.email.value,
        };
        axios.post(`/api`, userObject)
        .then(res => {
          console.log(res.status)
        }).catch(function(error) {
          console.log(error);
        });
      }
    
      render() {
        return (
          <div>Thank you! Your data was submitted successfully!</div>
          );
        }
      };
