import { Map, MapMarker } from 'react-kakao-maps-sdk';
import React, {useState, useMemo} from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from 'react-redux';
import { casesPoints, medPoints } from "../reducer/mapSlice";
const { kakao } = window; 

function LocalMap() {
    const dispatch = useDispatch()
    const [state, setState] = useState()
    const geoInfo = window.localStorage.getItem('sessionGeo').split(',')
    const [map, setMap] = useState()
    let points = useSelector(state => state.map.mapsState.map(
        x => {return {title: x.name,
                      latlng: {lat: x.latitude, lng:x.longitude}}}
    )) 
    
    return(<>
    <OptionUl>
        <li>
            <label>표시 대상 선택</label><br/>
            <select id="sel" onChange={(e) => {
            if (e.target.value == 'medpoint'){
                dispatch(medPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            }else{
                dispatch(casesPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            }
            console.log(points)
            // if (map) map.setBounds(bounds)
          }}>
                <optgroup label="표시 대상 선택">
                  <option value="medpoint">선별 진료소</option>  
                  <option value="cases">확진자 발생 위치</option>  
                </optgroup>
            </select>
        </li>
    </OptionUl>
    <Map // 지도를 표시할 Container
          center={{
            // 지도의 중심좌표
            lat: geoInfo[0],
            lng: geoInfo[1],
          }}
          style={{
            width: "1000px",
            height: "600px",
          }}
          level={5} // 지도의 확대 레벨
          onCreate={setMap}
        >
            <MapMarker
            position={{lat: geoInfo[0], lng:geoInfo[1]}}
            title='현재 위치'
            image={{
                // 무료 마커이미지의 주소: https://www.flaticon.com/kr/
                src: "https://cdn-icons.flaticon.com/png/512/2442/premium/2442108.png?token=exp=1637740148~hmac=014352e4a41371f036694412542fda4e", 
                size: {
                  width: 50,
                  height: 50,
                }, // 마커이미지의 크기입니다
                options: {
                  offset: {
                    x: 25,
                    y: 45,
                  }, // 마커이미지의 옵션입니다. 마커의 좌표와 일치시킬 이미지 안에서의 좌표를 설정합니다.
                },
              }}
            />
          {points.map((position, index) => (
            <MapMarker
            key={`${position.title}-${position.latlng}`}
            position={position.latlng}
            title={position.title}
            />
      ))}
        </Map>
        
    </>);
}

export default LocalMap;

const OptionUl = styled.ul`
    list-style: none;
`
