import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { medPoints, casesPoints } from "../reducer/mapSlice";
import styled from "styled-components";
const { kakao } = window;


function LocalMap() {
    const med_points = useSelector(state => state.map.mapsState)
    const dispatch = useDispatch()
    var geoInfo = window.localStorage.getItem('sessionGeo').split(',')

    const mapOption = { 
        // center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
        center: new kakao.maps.LatLng(geoInfo[0], geoInfo[1]), // 지도의 중심좌표
        level: 5 // 지도의 확대 레벨
    };
    const map = new kakao.maps.Map(document.getElementById('map'), mapOption);
    var bounds = new kakao.maps.LatLngBounds();
    var i, marker;
    const positions = med_points.map(x => {return {content: `<div style="padding:0 3em; border-radius: 15px;">${x.name}</div>`, 
    latlng: new kakao.maps.LatLng(x.latitude, x.longitude)}})
    for (var i = 0; i < positions.length; i ++) {
        // 마커를 생성합니다
        var marker = new kakao.maps.Marker({
            map: map, // 마커를 표시할 지도
            position: positions[i].latlng // 마커의 위치
        });
        marker.setMap(map)
        bounds.extend(positions[i].latlng)
        // kakao.maps.event.addListener(marker, 'mouseover', makeOverListener(map, marker, infowindow));
        // kakao.maps.event.addListener(marker, 'mouseout', makeOutListener(infowindow));
        // kakao.maps.event.addListener(marker, 'click', function(){alert('hi!')})
    }
    const getGeo =(e)=>{
        if (e.target.value == 'medpoint'){
            dispatch(medPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            map.setBounds(bounds);
        }else{
            dispatch(casesPoints({'latitude': geoInfo[0], 'longitude': geoInfo[1]}))
            map.setBounds(bounds);
        }
    }
    
    useEffect(() => {
    const geoInfo = window.localStorage.getItem('sessionGeo').split(',')
    // alert(geoInfo)
    // console.log(med_points)
    const mapContainer = document.getElementById('map'), // 지도를 표시할 div  
    mapOption = { 
        // center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
        center: new kakao.maps.LatLng(geoInfo[0], geoInfo[1]), // 지도의 중심좌표
        level: 5 // 지도의 확대 레벨
    };

    const map = new kakao.maps.Map(mapContainer, mapOption); // 지도를 생성합니다
    let positions = med_points.map(x => {return {content: `<div style="padding:0 3em; border-radius: 15px;">${x.name}</div>`, 
                                                 latlng: new kakao.maps.LatLng(x.latitude, x.longitude)}})
    positions = [...positions, {content: `<div style="padding:0 3em; border-radius: 15px;">내 위치</div>`, latlng: new kakao.maps.LatLng(geoInfo[0], geoInfo[1])}]
    // console.log(positions)
    for (let i = 0; i < positions.length; i ++) {
        // 마커를 생성합니다
        const marker = new kakao.maps.Marker({
            map: map, // 마커를 표시할 지도
            position: positions[i].latlng // 마커의 위치
        });

        // 마커에 표시할 인포윈도우를 생성합니다 
        const infowindow = new kakao.maps.InfoWindow({
            content: positions[i].content // 인포윈도우에 표시할 내용
        });

        // 마커에 mouseover 이벤트와 mouseout 이벤트를 등록합니다
        // 이벤트 리스너로는 클로저를 만들어 등록합니다 
        // for문에서 클로저를 만들어 주지 않으면 마지막 마커에만 이벤트가 등록됩니다
        kakao.maps.event.addListener(marker, 'mouseover', makeOverListener(map, marker, infowindow));
        kakao.maps.event.addListener(marker, 'mouseout', makeOutListener(infowindow));
        kakao.maps.event.addListener(marker, 'click', function(){alert('hi!')})
    }

    // 인포윈도우를 표시하는 클로저를 만드는 함수입니다 
    function makeOverListener(map, marker, infowindow) {
        return function() {
            infowindow.open(map, marker);
        };
    }

    // 인포윈도우를 닫는 클로저를 만드는 함수입니다 
    function makeOutListener(infowindow) {
        return function() {
            infowindow.close();
        };
    }
  }, []);

  return (<>
  <OptionUl>
        <li>
            <label>표시 대상 선택</label><br/>
            <select id="sel" onChange={getGeo}>
                <optgroup label="표시 대상 선택">
                  <option value="medpoint">선별 진료소</option>  
                  <option value="cases">확진자 발생 위치</option>  
                </optgroup>
            </select>
        </li>
    </OptionUl>
    <div
      className="map"
      id="map"
      style={{ width: "1000px", height: "500px", float: "left"}}
    ></div>
    </>);
}

export default LocalMap;

const OptionUl = styled.ul`
    list-style: none;
`
