import { Map, MapMarker } from 'react-kakao-maps-sdk';
import React, {useState, useMemo} from "react";
import styled from "styled-components";
import { useSelector } from 'react-redux';
const { kakao } = window; 

function LocalMap() {
    const [map, setMap] = useState()
    const points = useSelector(state => state.map.mapsState.map(
        x => {return {lat: x.latitude, lng:x.longitude}}
    )) 
    // const [points, setPoints] = useState([
    //   {lat: 33.452278, lng:126.567803},
    //   {lat: 33.452671, lng:126.574792},
    //   {lat: 33.451744, lng:126.572441},
    // ])


    const bounds = useMemo(() => {
      const bounds = new kakao.maps.LatLngBounds();

      points.forEach(point => {
        bounds.extend(new kakao.maps.LatLng(point.lat, point.lng))
      });
      return bounds;
    }, [points])

    return(<>
    <Map // 지도를 표시할 Container
          center={{
            // 지도의 중심좌표
            lat: 37.5885933100535,
            lng: 127.101500688631,
          }}
          style={{
            width: "1000px",
            height: "800px",
          }}
          level={3} // 지도의 확대 레벨
          onCreate={setMap}
        >
          {points.map(point => <MapMarker key={`${point.lat}-${point.lng}`} position={point} />)}
        </Map>
        <OptionUl>
        <li>
            <label>표시 대상 선택</label><br/>
            <select id="sel" onChange={() => {
            if (map) map.setBounds(bounds)
          }}>
                <optgroup label="표시 대상 선택">
                  <option value="medpoint">선별 진료소</option>  
                  <option value="cases">확진자 발생 위치</option>  
                </optgroup>
            </select>
        </li>
    </OptionUl>
    </>);
}

export default LocalMap;

const OptionUl = styled.ul`
    list-style: none;
`
