import React, {useEffect} from "react";
import { useDispatch } from "react-redux";
import { casesPoints, medPoints } from "../reducer/mapSlice";

const Geolocation = (props) => {
  const dispatch = useDispatch()
    useEffect(() => {
        if (navigator.geolocation) { // GPS를 지원하면
            navigator.geolocation.getCurrentPosition(function(position) {
            //   alert(position.coords.latitude + ' ' + position.coords.longitude);
            // alert(props)
            dispatch(casesPoints({'latitude': '37.55562896230684', 'longitude': '126.96922060615088'}))
            window.localStorage.setItem('sessionGeo', ['37.55562896230684', '126.96922060615088'])
            }, function(error) {
              console.error(error);
            }, {
              enableHighAccuracy: false,
              maximumAge: 0,
              timeout: Infinity
            });
          } else {
            alert('GPS를 지원하지 않습니다');
            // window.localStorage.setItem('sessionGeo', ['37.5885933100535', '127.101500688631'])
            window.localStorage.setItem('sessionGeo', ['37.55562896230684', '126.96922060615088'])
          }
    },[])
    return(<></>)
}

export default Geolocation
